import React, { useState, useEffect } from "react";
import Loading from "../../components/Loading";
import Form from "../../components/Form";
import FormStatic from "../../components/FormStatic";
import { useAuth0 } from "../../react-auth0-spa";

const axios = require("axios");

const Profile = () => {
  const { loading, user } = useAuth0();
  const [username_db, setUsername_db] = useState("");
  const [firstName_db, setFirstName_db] = useState("");
  const [lastName_db, setLastName_db] = useState("");
  const [newUser, setNew] = useState(true);

  useEffect(() => {
    axios.get("/user/email/" + user.email).then(res => {
      setUsername_db(res.data.username);
      setFirstName_db(res.data.firstName);
      setLastName_db(res.data.lastName);
      console.log(res.data.created);
      if (res.data.created !== undefined) {
        setNew(false);
      }
    });
  }, []);

  if (loading || !user) {
    return <Loading />;
  }

  return (
    <div class="twPc-div">
      <a class="twPc-bg twPc-block" />

      <div>
        <div class="twPc-button">
          <a
            href="https://twitter.com/mertskaplan"
            class="twitter-follow-button"
            data-show-count="false"
            data-size="large"
            data-show-screen-name="false"
            data-dnt="true"
          >
            Follow @mertskaplan
          </a>
        </div>

        <a
          title="Mert S. Kaplan"
          href="https://twitter.com/mertskaplan"
          class="twPc-avatarLink"
        >
          <img
            alt="Mert S. Kaplan"
            src="https://mertskaplan.com/wp-content/plugins/msk-twprofilecard/img/mertskaplan.jpg"
            class="twPc-avatarImg"
          />
          >
        </a>

        <div class="twPc-divUser">
          <div class="twPc-divName">
            <a href="https://twitter.com/mertskaplan">Mert S. Kaplan</a>
          </div>
          <span>
            <a href="https://twitter.com/mertskaplan">
              @<span>mertskaplan</span>
            </a>
          </span>
        </div>

        <div class="twPc-divStats">
          <ul class="twPc-Arrange">
            <li class="twPc-ArrangeSizeFit">
              <a href="https://twitter.com/mertskaplan" title="9.840 Tweet">
                <span class="twPc-StatLabel twPc-block">Tweets</span>
                <span class="twPc-StatValue">9.840</span>
              </a>
            </li>
            <li class="twPc-ArrangeSizeFit">
              <a
                href="https://twitter.com/mertskaplan/following"
                title="885 Following"
              >
                <span class="twPc-StatLabel twPc-block">Following</span>
                <span class="twPc-StatValue">885</span>
              </a>
            </li>
            <li class="twPc-ArrangeSizeFit">
              <a
                href="https://twitter.com/mertskaplan/followers"
                title="1.810 Followers"
              >
                <span class="twPc-StatLabel twPc-block">Followers</span>
                <span class="twPc-StatValue">1.810</span>
              </a>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Profile;

