import React, { useState } from "react";
import Loading from "../../components/Loading";
import { useAuth0 } from "../../react-auth0-spa";

var request = require("request");

var options = {
  method: "GET",
  url: "https://YOUR_DOMAIN/api/v2/users-by-email",
  qs: { email: "USER_EMAIL_ADDRESS" },
  headers: { authorization: "Bearer YOUR_MGMT_API_ACCESS_TOKEN" }
};

request(options, function(error, response, body) {
  if (error) throw new Error(error);

  console.log(body);
});

const axios = require("axios");

const User = () => {
  const { loading, user } = useAuth0();
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");

  if (loading || !user) {
    return <Loading />;
  }

  // const getUsers = () => {
  //   axios.get("/all/users").then(res => {
  //     console.log(res.data);
  //     setUsers(res.data);
  //   });
  // };

  const getUserById = id => {
    axios.get(`/user/${id}`).then(res => {
      setFirstName(res.data.firstName);
      setLastName(res.data.lastName);
    });
  };

  getUserById(1);

  return (
    <div className="container">
      <h1>{`Current User (from auth0): ${user.email}`}</h1>
      <div>
        {/* Check to see if any items are found*/}
        {user !== "" ? (
          <div>
            <p>{`${firstName} ${lastName} | ${user.email}`}</p>
            <hr />
          </div>
        ) : (
          <h3>No Results to Display</h3>
        )}
      </div>
    </div>
  );
};

export default User;
