import React, { useState } from "react";
import { confirmAlert } from "react-confirm-alert";
import "react-confirm-alert/src/react-confirm-alert.css";
import "./style.css";

const FormStatic = props => {
  const [disabled, setDisabled] = useState(true);
  const [edit, setEdit] = useState("Edit");

  const toggleEdit = () => {
    if (disabled) {
      setDisabled(false);
    } else {
      setDisabled(true);
    }
  };

  return (
    <>
      <form>
        <fieldset disabled={disabled}>
          <div className="form-group">
            <input
              className="form-control"
              value={props.username}
              placeholder="username"
              type="text"
              name="username"
              required
            />
          </div>
          <div className="form-group">
            <input
              className="form-control"
              value={props.firstName}
              placeholder="First name"
              type="text"
              name="firstName"
              required
            />
          </div>
          <div className="form-group">
            <input
              className="form-control"
              value={props.lastName}
              placeholder="Last name"
              type="text"
              name="lastName"
              required
            />
          </div>
          <div className="form-group">
            <input
              disabled
              readOnly
              className="form-control"
              value={props.email}
              placeholder="Email address"
              type="email"
              name="email"
            />
          </div>
        </fieldset>
        <input type="reset" className="btn btn-secondary m-1" value="Cancel" />
        <button className="btn btn-primary m-1">Save Changes</button>
      </form>
      <button className="btn btn-secondary m-1" onClick={toggleEdit}>
        {edit}
      </button>
    </>
  );
};

export default FormStatic;
